// Format successful response
const inputData = $input.all();
const method = $('Set Input Parameters').item.json.method;

const output = inputData.map(item => {
  return {
    json: {
      success: true,
      method: method,
      statusCode: item.json.statusCode || 200,
      data: item.json,
      timestamp: new Date().toISOString(),
      endpoint: 'devices'
    }
  };
});

return output;

// Format error response
const error = $input.all()[0].json;
const method = $('Set Input Parameters').item.json.method;

return [{
  json: {
    success: false,
    method: method,
    error: {
      message: error.message || 'Unknown error occurred',
      statusCode: error.statusCode || 500,
      details: error.error || error.description || null
    },
    timestamp: new Date().toISOString(),
    endpoint: 'devices'
  }
}];

// Validate required parameters
const method = $input.item.json.method || 'GET';
const deviceId = $input.item.json.deviceId;
const body = $input.item.json.body;

// Validation rules
const errors = [];

if (!['GET', 'POST', 'PUT', 'DELETE'].includes(method.toUpperCase())) {
  errors.push('Invalid HTTP method. Must be GET, POST, PUT, or DELETE');
}

if (['PUT', 'DELETE'].includes(method.toUpperCase()) && !deviceId) {
  errors.push('deviceId is required for PUT and DELETE operations');
}

if (['POST', 'PUT'].includes(method.toUpperCase())) {
  try {
    JSON.parse(body);
  } catch (e) {
    errors.push('Invalid JSON body for POST/PUT operation');
  }
}

if (errors.length > 0) {
  throw new Error(`Validation failed: ${errors.join(', ')}`);
}

return $input.all();
