// Build query string for lifecycle filters
const params = $input.item.json;
const queryParts = [];

if (params.deviceId) queryParts.push(`deviceId=${encodeURIComponent(params.deviceId)}`);
if (params.status) queryParts.push(`status=${encodeURIComponent(params.status)}`);
if (params.stage) queryParts.push(`stage=${encodeURIComponent(params.stage)}`);

const queryString = queryParts.length > 0 ? '?' + queryParts.join('&') : '';

return [{
  json: {
    ...params,
    queryString: queryString
  }
}];

// Format successful lifecycle response
const inputData = $input.all();
const params = $('Set Input Parameters').item.json;

const output = inputData.map(item => {
  const data = item.json;

  return {
    json: {
      success: true,
      method: params.method,
      statusCode: data.statusCode || 200,
      lifecycleId: params.lifecycleId || null,
      filters: {
        deviceId: params.deviceId || null,
        status: params.status || null,
        stage: params.stage || null
      },
      data: data,
      timestamp: new Date().toISOString(),
      endpoint: 'lifecycle'
    }
  };
});

return output;

// Format error response
const error = $input.all()[0].json;
const params = $('Set Input Parameters').item.json;

return [{
  json: {
    success: false,
    method: params.method || 'GET',
    lifecycleId: params.lifecycleId || null,
    error: {
      message: error.message || 'Lifecycle operation failed',
      statusCode: error.statusCode || 500,
      details: error.error || error.description || null
    },
    timestamp: new Date().toISOString(),
    endpoint: 'lifecycle'
  }
}];

// Validate lifecycle parameters
const params = $input.item.json;
const errors = [];
const validStatuses = ['pending', 'in-progress', 'completed', 'failed', 'cancelled'];
const validStages = ['discovery', 'provisioning', 'deployment', 'configuration', 'testing', 'production', 'decommission'];

if (!['GET', 'POST', 'PUT', 'DELETE'].includes(params.method.toUpperCase())) {
  errors.push('Invalid HTTP method');
}

if (['PUT', 'DELETE'].includes(params.method.toUpperCase()) && !params.lifecycleId) {
  errors.push('lifecycleId is required for PUT and DELETE operations');
}

if (params.status && !validStatuses.includes(params.status.toLowerCase())) {
  errors.push(`Invalid status. Must be one of: ${validStatuses.join(', ')}`);
}

if (params.stage && !validStages.includes(params.stage.toLowerCase())) {
  errors.push(`Invalid stage. Must be one of: ${validStages.join(', ')}`);
}

if (['POST', 'PUT'].includes(params.method.toUpperCase())) {
  try {
    const body = JSON.parse(params.body);
    if (params.method === 'POST' && !body.deviceId) {
      errors.push('deviceId is required in body for POST operation');
    }
    if (params.method === 'POST' && !body.stage) {
      errors.push('stage is required in body for POST operation');
    }
  } catch (e) {
    errors.push('Invalid JSON body');
  }
}

if (errors.length > 0) {
  throw new Error(`Validation failed: ${errors.join(', ')}`);
}

return $input.all();
