// Build onboarding query string
const params = $input.item.json;
const queryParts = [];

if (params.deviceId) queryParts.push(`deviceId=${encodeURIComponent(params.deviceId)}`);
if (params.status) queryParts.push(`status=${encodeURIComponent(params.status)}`);
if (params.template) queryParts.push(`template=${encodeURIComponent(params.template)}`);
if (params.autoApprove === 'true') queryParts.push(`autoApprove=true`);

const queryString = queryParts.length > 0 ? '?' + queryParts.join('&') : '';

return [{
  json: {
    ...params,
    queryString: queryString
  }
}];

// Format successful onboarding response
const inputData = $input.all();
const params = $('Set Input Parameters').item.json;

const output = inputData.map(item => {
  const data = item.json;

  // Calculate onboarding statistics if available
  let stats = null;
  if (Array.isArray(data)) {
    stats = {
      totalJobs: data.length,
      byStatus: data.reduce((acc, job) => {
        const status = job.status || 'unknown';
        acc[status] = (acc[status] || 0) + 1;
        return acc;
      }, {}),
      byTemplate: data.reduce((acc, job) => {
        const template = job.template || 'none';
        acc[template] = (acc[template] || 0) + 1;
        return acc;
      }, {})
    };
  }

  // Extract progress information if single job
  let progress = null;
  if (!Array.isArray(data) && data.steps) {
    const totalSteps = data.steps.length;
    const completedSteps = data.steps.filter(s => s.status === 'completed').length;
    progress = {
      totalSteps: totalSteps,
      completedSteps: completedSteps,
      percentage: totalSteps > 0 ? Math.round((completedSteps / totalSteps) * 100) : 0,
      currentStep: data.currentStep || null
    };
  }

  return {
    json: {
      success: true,
      method: params.method,
      statusCode: data.statusCode || 200,
      onboardingId: params.onboardingId || null,
      autoApproved: params.autoApprove === 'true',
      filters: {
        deviceId: params.deviceId || null,
        status: params.status || null,
        template: params.template || null
      },
      statistics: stats,
      progress: progress,
      data: data,
      timestamp: new Date().toISOString(),
      endpoint: 'onboarding'
    }
  };
});

return output;

// Format error response
const error = $input.all()[0].json;
const params = $('Set Input Parameters').item.json;

return [{
  json: {
    success: false,
    method: params.method || 'GET',
    onboardingId: params.onboardingId || null,
    autoApproved: params.autoApprove === 'true',
    error: {
      message: error.message || 'Onboarding operation failed',
      statusCode: error.statusCode || 500,
      details: error.error || error.description || null,
      failedStep: error.failedStep || null
    },
    timestamp: new Date().toISOString(),
    endpoint: 'onboarding'
  }
}];

// Validate onboarding parameters
const params = $input.item.json;
const errors = [];
const validStatuses = ['pending', 'in-progress', 'completed', 'failed', 'cancelled', 'awaiting-approval'];

if (!['GET', 'POST', 'PUT', 'DELETE'].includes(params.method.toUpperCase())) {
  errors.push('Invalid HTTP method');
}

if (['PUT', 'DELETE'].includes(params.method.toUpperCase()) && !params.onboardingId) {
  errors.push('onboardingId is required for PUT and DELETE operations');
}

if (params.status && !validStatuses.includes(params.status.toLowerCase())) {
  errors.push(`Invalid status. Valid statuses: ${validStatuses.join(', ')}`);
}

if (params.autoApprove && !['true', 'false'].includes(params.autoApprove.toLowerCase())) {
  errors.push('autoApprove must be true or false');
}

if (['POST', 'PUT'].includes(params.method.toUpperCase())) {
  try {
    const body = JSON.parse(params.body);

    if (params.method === 'POST') {
      // Validate required fields for new onboarding job
      if (!body.deviceIp && !body.deviceHostname) {
        errors.push('Either deviceIp or deviceHostname is required for POST operation');
      }
      if (!body.credentials) {
        errors.push('credentials object is required for POST operation');
      } else {
        if (!body.credentials.username) {
          errors.push('credentials.username is required');
        }
        if (!body.credentials.password && !body.credentials.sshKey) {
          errors.push('Either credentials.password or credentials.sshKey is required');
        }
      }
      if (body.template && typeof body.template !== 'string') {
        errors.push('template must be a string');
      }
    }

    if (params.method === 'PUT') {
      // Validate update operation
      if (!body.action) {
        errors.push('action is required in body for PUT operation (approve, retry, pause, resume)');
      } else if (!['approve', 'retry', 'pause', 'resume'].includes(body.action.toLowerCase())) {
        errors.push('Invalid action. Must be: approve, retry, pause, or resume');
      }
    }
  } catch (e) {
    errors.push('Invalid JSON body');
  }
}

if (errors.length > 0) {
  throw new Error(`Validation failed: ${errors.join(', ')}`);
}

return $input.all();
