// Parse GitHub PR event
const payload = $input.item.json;
const pr = payload.pull_request;
const repo = payload.repository;

// Only process opened and synchronize events
if (!['opened', 'synchronize', 'reopened'].includes(payload.action)) {
  console.log(`Skipping action: ${payload.action}`);
  return [];
}

// Extract PR details
const prData = {
  // PR Info
  pr_number: pr.number,
  pr_title: pr.title,
  pr_body: pr.body || '',
  pr_state: pr.state,
  pr_url: pr.html_url,
  pr_api_url: pr.url,
  action: payload.action,

  // Branch info
  head_branch: pr.head.ref,
  base_branch: pr.base.ref,
  head_sha: pr.head.sha,
  base_sha: pr.base.sha,

  // Repository info
  owner: repo.owner.login,
  repo_name: repo.name,
  full_repo_name: repo.full_name,

  // Author info
  author: pr.user.login,
  author_url: pr.user.html_url,

  // Stats
  changed_files: pr.changed_files,
  additions: pr.additions,
  deletions: pr.deletions,
  commits: pr.commits,

  // Dates
  created_at: pr.created_at,
  updated_at: pr.updated_at,

  // Metadata
  triggered_at: new Date().toISOString(),
  workflow_execution_id: $execution.id
};

console.log('PR Event detected:', {
  repository: prData.full_repo_name,
  pr_number: prData.pr_number,
  action: prData.action,
  pr_title: prData.pr_title,
  changed_files: prData.changed_files
});

return { json: prData };

// Filter for workflow JSON files and prepare for analysis
const prData = $input.first().json;
const files = $input.all().map(i => i.json) || [];

// Filter for workflow JSON files
const workflowFiles = files.filter(file => {
  return file.filename.endsWith('.json') &&
         (file.filename.includes('workflow') || 
          file.filename.startsWith('workflows/') ||
          file.status !== 'removed') &&
         !file.filename.includes('node_modules') &&
         !file.filename.includes('package');
});

if (workflowFiles.length === 0) {
  console.log('No workflow files changed in this PR');
  return [{
    json: {
      ...prData,
      has_workflow_changes: false,
      workflow_files: [],
      skip_review: true,
      message: 'No workflow files changed in this PR'
    }
  }];
}

console.log(`Found ${workflowFiles.length} workflow file(s) to review`);

// Structure file metadata
const structuredFiles = workflowFiles.map(file => ({
  filename: file.filename,
  status: file.status,
  additions: file.additions,
  deletions: file.deletions,
  changes: file.changes,
  patch: file.patch,
  raw_url: file.raw_url,
  blob_url: file.blob_url,
  previous_filename: file.previous_filename
}));

return [{
  json: {
    ...prData,
    has_workflow_changes: true,
    workflow_files: structuredFiles,
    workflow_file_count: workflowFiles.length,
    skip_review: false
  }
}];

// Build comprehensive context for AI analysis
const data = $input.item.json;

// Generate detailed summary of changes
const filesSummary = data.workflow_files.map(file => {
  const status = {
    added: 'âž• Added',
    modified: 'ðŸ“ Modified', 
    removed: 'âŒ Removed',
    renamed: 'ðŸ”„ Renamed'
  }[file.status] || file.status;

  return `${status}: ${file.filename} (+${file.additions}/-${file.deletions})`;
}).join('\n');

// Build analysis prompt
const analysisPrompt = `You are an expert n8n workflow engineer and code reviewer. Please analyze this pull request containing n8n workflow changes.

## Pull Request Information
**Title:** ${data.pr_title}
**Author:** ${data.author}
**Repository:** ${data.full_repo_name}
**Branch:** ${data.head_branch} â†’ ${data.base_branch}

**Description:**
${data.pr_body || 'No description provided'}

## Files Changed (${data.workflow_file_count} workflow files)
${filesSummary}

## Statistics
- Total changes: ${data.additions} additions, ${data.deletions} deletions
- Total files: ${data.changed_files}
- Commits: ${data.commits}

## Workflow File Changes
${data.workflow_files.map((file, idx) => `
### File ${idx + 1}: ${file.filename}
**Status:** ${file.status}
**Changes:** +${file.additions} -${file.deletions}

**Diff:**
\`\`\`diff
${file.patch || 'No patch available'}
\`\`\`
`).join('\n---\n')}

## Review Requirements
Please provide a comprehensive review with the following sections:

### 1. Summary
Provide a concise summary of what this PR accomplishes.

### 2. Risk Analysis
Assess the risk level (Low/Medium/High) and explain:
- Potential breaking changes
- Impact on existing workflows
- Security considerations
- Data handling concerns

### 3. Code Quality Assessment
Evaluate:
- Workflow structure and organization
- Node configuration best practices
- Error handling implementation
- Modularity and reusability
- Naming conventions

### 4. Specific Observations
Highlight:
- Well-implemented patterns
- Potential issues or bugs
- Performance considerations
- Missing error handlers
- Hardcoded values that should be variables

### 5. Recommendations
Provide actionable suggestions for:
- Improvements to workflow design
- Better error handling
- Security enhancements
- Performance optimizations
- Documentation needs

### 6. Testing Checklist
Suggest what should be tested:
- [ ] List specific test scenarios
- [ ] Edge cases to verify
- [ ] Integration points to check

### 7. Overall Assessment
Provide:
- Approval status: APPROVE, REQUEST_CHANGES, or COMMENT
- Overall quality score (1-10)
- Key action items for the author

Format your response in clear GitHub Markdown that can be posted as a PR comment.`;

return {
  json: {
    ...data,
    analysis_prompt: analysisPrompt,
    prompt_length: analysisPrompt.length,
    ready_for_ai: true
  }
};

// Extract and structure AI review response
const data = $input.item.json;
const aiResponse = data.content[0].text;

// Parse the response to extract key sections
const extractSection = (text, sectionName) => {
  const regex = new RegExp(`### \\d+\\.\\s*${sectionName}[\\s\\S]*?(?=###|$)`, 'i');
  const match = text.match(regex);
  return match ? match[0].replace(/^### \d+\.\s*\w+\s*/, '').trim() : '';
};

// Extract risk level
const riskMatch = aiResponse.match(/Risk.*?:?\s*(Low|Medium|High)/i);
const riskLevel = riskMatch ? riskMatch[1] : 'Medium';

// Extract approval status
const approvalMatch = aiResponse.match(/APPROVE|REQUEST_CHANGES|COMMENT/i);
const approvalStatus = approvalMatch ? approvalMatch[0].toUpperCase() : 'COMMENT';

// Extract quality score
const scoreMatch = aiResponse.match(/score.*?(\d+)\s*\/\s*10/i);
const qualityScore = scoreMatch ? parseInt(scoreMatch[1]) : null;

const structuredReview = {
  // Original data
  pr_number: data.pr_number,
  pr_title: data.pr_title,
  full_repo_name: data.full_repo_name,
  owner: data.owner,
  repo_name: data.repo_name,

  // AI Analysis
  review_text: aiResponse,
  review_summary: extractSection(aiResponse, 'Summary'),
  risk_analysis: extractSection(aiResponse, 'Risk Analysis'),
  recommendations: extractSection(aiResponse, 'Recommendations'),

  // Parsed metadata
  risk_level: riskLevel,
  approval_status: approvalStatus,
  quality_score: qualityScore,

  // Statistics
  review_length: aiResponse.length,
  tokens_used: data.usage?.total_tokens || 0,

  // Timestamps
  reviewed_at: new Date().toISOString(),

  // Format for GitHub
  github_comment_body: `## ðŸ¤– AI Workflow Review\n\n${aiResponse}\n\n---\n\n*Reviewed by Claude AI | ${new Date().toISOString()}*\n*Review ID: ${$execution.id}*`
};

console.log('AI Review completed:', {
  risk_level: structuredReview.risk_level,
  approval_status: structuredReview.approval_status,
  quality_score: structuredReview.quality_score,
  review_length: structuredReview.review_length
});

return { json: structuredReview };

// Generate final response with review summary
const reviewData = $input.item.json;

const response = {
  status: 'success',
  pr_number: reviewData.pr_number,
  repository: reviewData.full_repo_name,

  review: {
    risk_level: reviewData.risk_level,
    approval_status: reviewData.approval_status,
    quality_score: reviewData.quality_score,
    review_length: reviewData.review_length,
    tokens_used: reviewData.tokens_used
  },

  actions_taken: {
    comment_posted: true,
    review_submitted: true,
    labels_added: true
  },

  metadata: {
    reviewed_at: reviewData.reviewed_at,
    execution_id: $execution.id,
    workflow_id: $workflow.id
  }
};

console.log('PR Review workflow completed:', response);

return { json: response };

